(2743, 475, '_team_section_button_text', 'field_6304cc0eb1519'),
(2744, 475, 'team_section_button_link', 'a:3:{s:5:\"title\";s:8:\"Our team\";s:3:\"url\";s:43:\"http://localhost:8888/bematech-wp/our-team/\";s:6:\"target\";s:0:\"\";}'),
(2745, 475, '_team_section_button_link', 'field_6304cc1bb151a'),
(2746, 475, 'cta_block_title', 'Sie benötigen unsere Hilfe?'),
(2747, 475, '_cta_block_title', 'field_6304c26943830'),
(2748, 475, 'cta_block_text', 'Es passiert immer im ungünstigsten Moment: der Drucker streikt, der Toner im Kopierer geht zur Neige oder der Scanner hat seine Verbindung zum Firmennetzwerk verloren. Kein Grund zur Panik, denn unsere Mitarbeiter sind nur einen Anruf entfernt – für sauberen Service auf Boxenstopp-Niveau.'),
